COLLECT STATS ON $$STGDB.WI_AR_SMC_ATTRIB_NRTR_CHNG_CHK;
DELETE FROM $$STGDB.WI_AR_SMC_ATTRIB_NRTR_CHNG_CHK WHERE ( SALES_ORDER_LINE_KEY, DV_ENTERPRISE_INV_SKU_ID ) IN ( SEL SALES_ORDER_LINE_KEY, DV_ENTERPRISE_INV_SKU_ID FROM $$STGDB.WI_AR_SMC_ATTRIB_NRTR_CHNG_CHK GROUP BY 1,2 HAVING ROUND(SUM(DV_ALLOCATION_PCT),4) <> 1.0000 ) ;
DELETE FROM $$STGDB.WI_MT_AR_SMC_ATTRIB_NRTR WI WHERE EXISTS ( SEL 1 FROM $$STGDB.WI_AR_SMC_ATTRIB_NRTR_CHNG_CHK NO_CHNG WHERE WI.SALES_ORDER_LINE_KEY = NO_CHNG.SALES_ORDER_LINE_KEY AND WI.DV_ENTERPRISE_INV_SKU_ID = NO_CHNG.DV_ENTERPRISE_INV_SKU_ID ) ; /* END: SMR Aug 16th changes - To prevent unnecessary changes going into MT table */
UPDATE MT FROM $$SLSORDDB.MT_AR_SLS_MOTION_ATTRIB MT , ( SELECT SALES_ORDER_LINE_KEY, DV_ENTERPRISE_INV_SKU_ID , MAX(START_TV_DTM) AS START_TV_DTM FROM $$STGDB.WI_MT_AR_SMC_ATTRIB_NRTR GROUP BY 1,2 ) WI SET END_TV_DTM = WI.START_TV_DTM - INTERVAL '1' SECOND, EDW_UPDATE_DTM = CURRENT_TIMESTAMP(0) WHERE MT.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY AND MT.DV_ENTERPRISE_INV_SKU_ID = WI.DV_ENTERPRISE_INV_SKU_ID AND MT.START_TV_DTM <> WI.START_TV_DTM AND MT.END_TV_DTM = '3500-01-01 00:00:00' ;

