TABLEATTRIBUTE.VALUE:
SELECT SMC_FINAL.SALES_ORDER_LINE_KEY, SMC_FINAL.SALES_MOTION_CD, SMC_FINAL.DV_ALLOCATION_PCT/SMC_FINAL.SUM_SPLIT AS DV_ALLOCATION_PCT, SMC_FINAL.DV_SERVICE_CATEGORY_CD, SMC_FINAL.START_TV_DTM , SMC_FINAL.END_TV_DTM, SMC_FINAL.SALES_MOTION_TIMING_CD SALES_MOTION_TIMING_CD, SMC_FINAL.DV_SOURCE_TYPE DV_SOURCE_TYPE, SMC_FINAL.RENEWAL_GAP_DAYS FROM (SELECT SMC.SALES_ORDER_LINE_KEY, SMC.SALES_MOTION_CD, SUM(SMC.DV_ALLOCATION_PCT ) AS DV_ALLOCATION_PCT, SMC.SUM_SPLIT , smc.DV_SERVICE_CATEGORY_CD, SMC.START_TV_DTM , SMC.END_TV_DTM, SMC.SALES_MOTION_TIMING_CD SALES_MOTION_TIMING_CD, SMC.DV_SOURCE_TYPE DV_SOURCE_TYPE,/* DV SOURCE TYPE ADDITION to accommodate SQ */ SMC.RENEWAL_GAP_DAYS FROM ( /* For RTR data */ SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_ALLOCATION_PCT, CASE WHEN SUM_SPLIT=0 THEN 1 ELSE SUM_SPLIT END AS SUM_SPLIT, DV_SERVICE_CATEGORY_CD, START_TV_DTM, END_TV_DTM, SALES_MOTION_TIMING_CD, CAST('RTNR' AS VARCHAR(10)) AS DV_SOURCE_TYPE, RENEWAL_GAP_DAYS FROM (SELECT RTR.SALES_ORDER_LINE_KEY, RTR.SALES_MOTION_CD, RTR.ATTR_PCT DV_ALLOCATION_PCT, SUM(RTR.ATTR_PCT) OVER(PARTITION BY RTR.SALES_ORDER_LINE_KEY) SUM_SPLIT, MAX(RTR.START_TV_DTM) OVER(PARTITION BY RTR.SALES_ORDER_LINE_KEY) START_TV_DTM, /* Changed logic as part of May 2nd release */ CAST('3500-01-01 00:00:00' AS TIMESTAMP(0)) AS END_TV_DTM, RTR.ITEM_CATEGORY_NAME AS DV_SERVICE_CATEGORY_CD, RTR.SALES_MOTION_TIMING_CD, DV_RENEWAL_GAP_DAYS_CNT AS RENEWAL_GAP_DAYS FROM $$SLSORDVWDB.N_ATTR_PRDT_NEW_RNWL_ACV_TV RTR WHERE END_TV_DTM = '3500-01-01 00:00:00' AND RTR.ITEM_CATEGORY_NAME NOT IN ( 'Billing', 'FLEXIBLE CONSUMPTION MODEL' ) AND SALES_ORDER_LINE_KEY IN ( SEL SALES_ORDER_LINE_KEY FROM $$STGDB.WI_MT_SMC_ATTRIBUTION_INCR INCR WHERE DV_SOURCE_TYPE = 'RTNR' ) ) SMC1
/* Below for SQ data */ UNION ALL
SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_ALLOCATION_PCT, CASE WHEN SUM_SPLIT=0 THEN 1 ELSE SUM_SPLIT END AS SUM_SPLIT, DV_SERVICE_CATEGORY_CD, START_TV_DTM, END_TV_DTM, SALES_MOTION_TIMING_CD, CAST( 'SQ' AS VARCHAR(10)) AS DV_SOURCE_TYPE, RENEWAL_GAP_DAYS FROM (SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_ALLOCATION_PCT, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY) SUM_SPLIT, DV_SERVICE_CATEGORY_CD, MAX(START_TV_DTM) OVER(PARTITION BY SALES_ORDER_LINE_KEY) START_TV_DTM, /* Changed logic as part of May 2nd release */ MAX(END_TV_DTM) OVER(PARTITION BY SALES_ORDER_LINE_KEY) END_TV_DTM, SALES_MOTION_TIMING_CD, RENEWAL_GAP_DAYS FROM $$STGDB.WI_MT_SMC_ATTRIBUTION_INCR INCR WHERE INCR.DV_SOURCE_TYPE = 'SQ' ) SMC1 ) SMC GROUP BY 1,2,4,5,6,7,8,9,10 ) SMC_FINAL

ATTRIBUTE.VALUE:
CREATE MULTISET VOLATILE TABLE WI_ZERO_SPLIT_LINES AS ( SEL SALES_ORDER_LINE_KEY, SUM(DV_ALLOCATION_PCT) SUM_SPLIT, COUNT(*) REC_CNT FROM $$STGDB.WI_RTNR_SMC_ALLOCATION WHERE END_TV_DTM > CURRENT_TIMESTAMP(0) GROUP BY 1 HAVING ROUND(SUM_SPLIT, 3) = 0.00000 ) WITH DATA ON COMMIT PRESERVE ROWS ;
UPDATE MT FROM $$STGDB.WI_RTNR_SMC_ALLOCATION MT, ( SEL WI.SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_ALLOCATION_PCT, CAST( 1.000000/REC_CNT AS DECIMAL(18,6)) DV_ALLOCATION_PCT1 FROM $$STGDB.WI_RTNR_SMC_ALLOCATION WI INNER JOIN WI_ZERO_SPLIT_LINES ZERO ON WI.SALES_ORDER_LINE_KEY = ZERO.SALES_ORDER_LINE_KEY WHERE WI.END_TV_DTM > CURRENT_TIMESTAMP(0) ) WI SET DV_ALLOCATION_PCT = WI.DV_ALLOCATION_PCT1 WHERE MT.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY AND MT.SALES_MOTION_CD = WI.SALES_MOTION_CD AND MT.END_TV_DTM > CURRENT_TIMESTAMP(0) AND MT.DV_ALLOCATION_PCT <> WI.DV_ALLOCATION_PCT1 ;
CALL COLLECT_STATS_WRAP('$$STGDB','WI_RTNR_SMC_ALLOCATION','D');
