ATTRIBUTE.VALUE:
/* START: SMR Aug 16th changes - To prevent unnecessary changes going into MT table */ DELETE FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK ;
INSERT INTO $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK SELECT MANUAL_TRX_KEY ,SALES_ORDER_LINE_KEY ,DV_ENTERPRISE_INV_SKU_ID ,SALES_MOTION_CD ,DV_ALLOCATION_PCT ,DV_SERVICE_CATEGORY_CD ,DV_OA_FLG ,START_TV_DTM ,END_TV_DTM ,DV_SOURCE_TYPE ,SALES_MOTION_TIMING_CD ,AR_TRX_LINE_KEY FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WI WHERE EXISTS ( SEL 1 FROM $$SLSORDVWDB.MT_SLS_ADJ_SLS_MOTION_ATTRIB MT_ADJ WHERE MT_ADJ.END_TV_DTM = '3500-01-01 00:00:00' AND WI.MANUAL_TRX_KEY = MT_ADJ.MANUAL_TRX_KEY AND WI.SALES_ORDER_LINE_KEY = MT_ADJ.SALES_ORDER_LINE_KEY AND WI.DV_ENTERPRISE_INV_SKU_ID = MT_ADJ.DV_ENTERPRISE_INV_SKU_ID AND WI.SALES_MOTION_CD = MT_ADJ.SALES_MOTION_CD AND WI.DV_ALLOCATION_PCT = MT_ADJ.DV_ALLOCATION_PCT AND WI.DV_SERVICE_CATEGORY_CD = MT_ADJ.DV_SERVICE_CATEGORY_CD AND WI.DV_SOURCE_TYPE = MT_ADJ.DV_SOURCE_TYPE ) ;
COLLECT STATS ON $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK;
DELETE FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK WHERE ( MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID ) IN ( SEL MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK WHERE AR_TRX_LINE_KEY < 0 GROUP BY 1,2 HAVING ROUND(SUM(DV_ALLOCATION_PCT),4) <> 1.0000 ) ;
DELETE FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK WHERE ( MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID, SALES_ORDER_LINE_KEY ) IN ( SEL MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID, SALES_ORDER_LINE_KEY FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK WHERE AR_TRX_LINE_KEY > 0 GROUP BY 1,2,3 HAVING ROUND(SUM(DV_ALLOCATION_PCT),4) <> 1.0000 ) ;
DELETE FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WI WHERE EXISTS ( SEL 1 FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK NO_CHNG WHERE NO_CHNG.AR_TRX_LINE_KEY < 0 AND WI.MANUAL_TRX_KEY = NO_CHNG.MANUAL_TRX_KEY AND WI.DV_ENTERPRISE_INV_SKU_ID = NO_CHNG.DV_ENTERPRISE_INV_SKU_ID ) ;
DELETE FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WI WHERE EXISTS ( SEL 1 FROM $$STGDB.WI_ADJ_SMC_ATTRIB_RTR_CHNG_CHK NO_CHNG WHERE NO_CHNG.AR_TRX_LINE_KEY > 0 AND WI.MANUAL_TRX_KEY = NO_CHNG.MANUAL_TRX_KEY AND WI.DV_ENTERPRISE_INV_SKU_ID = NO_CHNG.DV_ENTERPRISE_INV_SKU_ID AND WI.SALES_ORDER_LINE_KEY = NO_CHNG.SALES_ORDER_LINE_KEY ) ;
/* END: SMR Aug 16th changes - To prevent unnecessary changes going into MT table */
UPDATE MT FROM $$SLSORDVWDB.MT_SLS_ADJ_SLS_MOTION_ATTRIB MT , ( SELECT MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID, MAX(START_TV_DTM) MAX_START_DTM FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WHERE AR_TRX_LINE_KEY < 0 /* Added condition to process AR rebates separately in next pipeline*/ GROUP BY 1,2 ) WI SET END_TV_DTM = MAX_START_DTM - INTERVAL '1' SECOND, EDW_UPDATE_DTM = CURRENT_TIMESTAMP(0) WHERE MT.MANUAL_TRX_KEY = WI.MANUAL_TRX_KEY AND MT.DV_ENTERPRISE_INV_SKU_ID = WI.DV_ENTERPRISE_INV_SKU_ID AND MT.START_TV_DTM <> WI.MAX_START_DTM AND MT.END_TV_DTM = '3500-01-01 00:00:00';

DELETE FROM $$STGDB.WI_MTK_WITH_ZERO_SPLIT;
INSERT INTO $$STGDB.WI_MTK_WITH_ZERO_SPLIT SELECT MANUAL_TRX_KEY, DV_ENTERPRISE_INV_SKU_ID, 1.000000 AS DV_ALLOCATION_PCT , COUNT(*) CNT_REC FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WHERE SALES_ORDER_LINE_KEY> 0 AND AR_TRX_LINE_KEY < 0 GROUP BY 1,2,3 HAVING SUM(DV_ALLOCATION_PCT) = 0 ;
COLLECT STATISTICS COLUMN (MANUAL_TRX_KEY,DV_ENTERPRISE_INV_SKU_ID ) ON $$STGDB.WI_MTK_WITH_ZERO_SPLIT;
UPDATE WI FROM $$STGDB.WI_MT_SLS_ADJ_SMC_ATTRIB_RTR WI , $$STGDB.WI_MTK_WITH_ZERO_SPLIT ST SET DV_ALLOCATION_PCT = ST.DV_ALLOCATION_PCT/CNT_REC WHERE WI.MANUAL_TRX_KEY = ST.MANUAL_TRX_KEY AND WI.DV_ENTERPRISE_INV_SKU_ID = ST.DV_ENTERPRISE_INV_SKU_ID AND WI.SALES_ORDER_LINE_KEY > 0 AND AR_TRX_LINE_KEY < 0;
CALL COLLECT_STATS_WRAP('$$STGDB','WI_MT_SLS_ADJ_SMC_ATTRIB_RTR','D');
