ATTRIBUTE.VALUE:
CALL COLLECT_STATS_WRAP('$$STGDB','WI_AR_SUMMARY_QUOTE_SMR','D');
DELETE FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMR_PRE WI WHERE EXISTS( SEL 1 FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMR PRE WHERE PRE.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY AND PRE.SALES_MOTION_CD <> WI.SALES_MOTION_CD );
INSERT INTO $$STGDB.WI_AR_SUMMARY_QUOTE_SMR_PRE SELECT SALES_ORDER_LINE_KEY , DV_EFFECTIVE_DTM , SALES_MOTION_CD , EDW_CREATE_USER , EDW_CREATE_DTM FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMR;
CALL COLLECT_STATS_WRAP('$$STGDB','WI_AR_SUMMARY_QUOTE_SMR_PRE','D');
UPDATE EL FROM $$ETLVWDB.CTL_ETL_LAST_EXTRACT_DATES EL, ( SELECT MAX(EDW_UPDATE_DTM) MAX_UPD_DATE FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC ) NSOL SET LAST_EXTRACT_DATE = COALESCE(NSOL.MAX_UPD_DATE , EL.LAST_EXTRACT_DATE ) WHERE JOB_STREAM_ID = 'wf_WI_REV_SCA_NRT_SMR_TV' AND TABLE_NAME = 'WI_AR_SUMMARY_QUOTE_SMR';

INSERT INTO $$STGDB.WI_REV_SCA_NRT_SMR_INCR; SELECT APPL.SALES_CREDIT_ASSIGNMENT_KEY , APPL.SCA_SOURCE_TYPE_CD , APPL.SK_LINE_SEQ_ID_INT , APPL.SS_CD , APPL.EP_SRC_LINE_ID_INT , APPL.EP_SK_SALESREP_ID_INT , APPL.SCA_SALES_COMMISSION_PCT , APPL.START_TV_DTM , APPL.END_TV_DTM , APPL.DV_SCA_SRC_COMMIT_DT , APPL.EDW_CREATE_DTM , APPL.EDW_UPDATE_DTM , APPL.SALES_ORDER_LINE_KEY , APPL.RU_AR_TRANSACTION_LINE_KEY , APPL.EP_SK_SALES_CREDIT_TYPE_ID_INT, APPL.EP_SK_TERRITORY_ID_INT , CASE WHEN APPL.SS_CD = 'CG' THEN NSCTT.SALES_CREDIT_TYPE_QUOTA_FLAG ELSE ESCT.QUOTA_FLAG END AS QUOTA_FLAG, CASE WHEN APPL.SS_CD = 'CG' THEN APPL.BK_SALES_CREDIT_TYPE_CODE ELSE ESCT.NAME END AS BK_SALES_CREDIT_TYPE_CODE, CASE WHEN ( NFISMN.BK_FISCAL_MONTH_NUMBER_INT+NFISMN.BK_FISCAL_YEAR_NUMBER_INT *100 ) < (NFISMN_SYS.BK_FISCAL_MONTH_NUMBER_INT+NFISMN_SYS.BK_FISCAL_YEAR_NUMBER_INT *100) THEN NFISMN_SYS.BK_FISCAL_MONTH_NUMBER_INT ELSE NFISMN.BK_FISCAL_MONTH_NUMBER_INT END AS BK_FISCAL_MONTH_NUMBER_INT , CASE WHEN ( NFISMN.BK_FISCAL_MONTH_NUMBER_INT+NFISMN.BK_FISCAL_YEAR_NUMBER_INT *100 ) < (NFISMN_SYS.BK_FISCAL_MONTH_NUMBER_INT+NFISMN_SYS.BK_FISCAL_YEAR_NUMBER_INT *100) THEN NFISMN_SYS.BK_FISCAL_YEAR_NUMBER_INT ELSE NFISMN.BK_FISCAL_YEAR_NUMBER_INT END AS BK_FISCAL_YEAR_NUMBER_INT , CASE WHEN ( NFISMN.BK_FISCAL_MONTH_NUMBER_INT+NFISMN.BK_FISCAL_YEAR_NUMBER_INT *100 ) < (NFISMN_SYS.BK_FISCAL_MONTH_NUMBER_INT+NFISMN_SYS.BK_FISCAL_YEAR_NUMBER_INT *100) THEN (NFISMN_SYS.BK_FISCAL_MONTH_NUMBER_INT+NFISMN_SYS.BK_FISCAL_YEAR_NUMBER_INT *100) ELSE ( NFISMN.BK_FISCAL_MONTH_NUMBER_INT+NFISMN.BK_FISCAL_YEAR_NUMBER_INT *100 ) END AS DV_FISCAL_YEAR_MONTH_NUM_INT , NULL AS EVENT_TYPE , APPL.SALES_REP_NUMBER , APPL.SALES_TERRITORY_KEY , APPL.SK_SC_AGENT_ID_INT , APPL.SK_RTR_ATTRIBUTION_ID , APPL.TOTAL_SPLIT_PCT , APPL.SALES_MOTION_CD , APPL.BK_OFFER_ATTRIBUTION_ID_INT FROM ( SELECT BK_FISCAL_MONTH_NUMBER_INT, BK_FISCAL_YEAR_NUMBER_INT FROM $$COMREFVWDB.N_FISCAL_MONTH WHERE Current_Date-1 BETWEEN FISCAL_MONTH_START_DATE AND FISCAL_MONTH_END_DATE ) NFISMN_SYS , $$NRTNCRVWDB.N_SCA_FR_ALL_TRX_NRT_HST_NG_TV APPL INNER JOIN STGDB_TS3.WI_AR_SUMMARY_QUOTE_SMR SMR ON SMR.SALES_ORDER_LINE_KEY = APPL.SALES_ORDER_LINE_KEY INNER JOIN $$COMREFVWDB.N_SOURCE_SYSTEM_CODES NSSC ON APPL.SS_CD = NSSC.SOURCE_SYSTEM_CODE LEFT OUTER JOIN $$ETLVWDB.EL_SALES_CREDIT_TYPES ESCT ON ESCT.SK_CREDIT_TYPE_ID = APPL.EP_SK_SALES_CREDIT_TYPE_ID_INT AND ESCT.GLOBAL_NAME = NSSC.GLOBAL_NAME LEFT OUTER JOIN ( SELECT BK_SALES_CREDIT_TYPE_CODE, SALES_CREDIT_TYPE_QUOTA_FLAG FROM $$SLSORDVWDB.N_SALES_CREDIT_TYPE_TV QUALIFY Row_Number () Over (PARTITION BY BK_SALES_CREDIT_TYPE_CODE ORDER BY END_TV_DATE DESC)=1 ) NSCTT ON APPL.BK_SALES_CREDIT_TYPE_CODE = NSCTT.BK_SALES_CREDIT_TYPE_CODE INNER JOIN $$COMREFVWDB.N_FISCAL_MONTH NFISMN ON APPL.SCA_SRC_COMMIT_DTM BETWEEN NFISMN.FISCAL_MONTH_START_DATE AND NFISMN.FISCAL_MONTH_END_DATE WHERE 1=1 AND EXISTS ( /* case-1 - when a match is found in N_AR_BATCH_SOURCE - then it must NOT be XAAS related - THIS SHOULD BE ALLOWED TO FLOW THROUGH */ SELECT 1 FROM $$FINLGLVWDB.N_AR_TRX_LINE NART INNER JOIN $$FINLGLVWDB.N_AR_TRX NAT ON NART.AR_TRX_KEY = NAT.AR_TRX_KEY INNER JOIN $$FINLGLVWDB.N_AR_BATCH_SOURCE NABS ON NAT.AR_BATCH_SOURCE_KEY = NABS.AR_BATCH_SOURCE_KEY WHERE 1=1 AND NART.AR_TRX_LINE_KEY=APPL.RU_AR_TRANSACTION_LINE_KEY AND (NABS.BK_BATCH_SOURCE_NAME NOT LIKE ('%WEBEX%') AND ( NABS.BK_BATCH_SOURCE_NAME NOT LIKE ALL ('%XAAS%','CSP-MARKETPLACE') OR EXISTS (SELECT 1 FROM $$ETLVWDB.EL_XAAS_TRX_FROM_ERP_SC EL WHERE EL.AR_TRX_LINE_KEY = NART.AR_TRX_LINE_KEY) /* Q2FY19 Dec Rel : Included above cond. to consider XAAS trx from ERP SC */ ) ) ) /* Not to pull any incremental records for which no SC changes are there */ AND NOT EXISTS ( SELECT 1 FROM $$STGDB.WI_REV_SCA_NRT_SMR_INCR INC WHERE INC.RU_SALES_ORDER_LINE_KEY = APPL.SALES_ORDER_LINE_KEY)
AND NOT EXISTS ( SEL 1 FROM $$STGDB.WI_REV_SCA_NRT_SMR_PRE PRE WHERE (CASE WHEN APPL.SALES_ORDER_LINE_KEY > 0 THEN APPL.SALES_ORDER_LINE_KEY ELSE -9999 END) = (CASE WHEN PRE.RU_SALES_ORDER_LINE_KEY > 0 THEN PRE.RU_SALES_ORDER_LINE_KEY ELSE -9999 END) AND APPL.RU_AR_TRANSACTION_LINE_KEY=PRE.RU_AR_TRANSACTION_LINE_KEY AND APPL.SALES_TERRITORY_KEY=PRE.SALES_TERRITORY_KEY AND APPL.SALES_REP_NUMBER=PRE.SALES_REP_NUMBER AND APPL.SK_RTR_ATTRIBUTION_ID=PRE.SK_RTR_ATTRIBUTION_ID AND APPL.BK_OFFER_ATTRIBUTION_ID_INT =PRE.BK_OFFER_ATTRIBUTION_ID_INT AND APPL.TOTAL_SPLIT_PCT =PRE.TOTAL_SPLIT_PCT AND APPL.SALES_MOTION_CD =PRE.SALES_MOTION_CD );
CALL COLLECT_STATS_WRAP('$$STGDB','WI_REV_SCA_NRT_SMR_INCR','D');
DELETE FROM $$STGDB.WI_REV_SCA_NRT_SMR_PRE PRE WHERE EXISTS( SEL 1 FROM $$STGDB.WI_REV_SCA_NRT_SMR_INCR INCR WHERE PRE.RU_SALES_ORDER_LINE_KEY = INCR.RU_SALES_ORDER_LINE_KEY AND PRE.RU_SALES_ORDER_LINE_KEY>0);
DELETE FROM $$STGDB.WI_REV_SCA_NRT_SMR_PRE PRE WHERE EXISTS( SEL 1 FROM $$STGDB.WI_REV_SCA_NRT_SMR_INCR INCR WHERE PRE.RU_AR_TRANSACTION_LINE_KEY = INCR.RU_AR_TRANSACTION_LINE_KEY AND PRE.RU_AR_TRANSACTION_LINE_KEY>0);
INS INTO $$STGDB.WI_REV_SCA_NRT_SMR_PRE SELECT SALES_CREDIT_ASSIGNMENT_KEY , SCA_SOURCE_TYPE_CD , SK_LINE_SEQ_ID_INT , SS_CD , EP_SOURCE_LINE_ID_INT , EP_SK_SALESREP_ID_INT , SCA_SALES_COMMISSION_PCT , START_TV_DTM , END_TV_DTM , SCA_SOURCE_COMMIT_DTM , EDW_CREATE_DTM , EDW_UPDATE_DTM , RU_SALES_ORDER_LINE_KEY , RU_AR_TRANSACTION_LINE_KEY , EP_SK_SALES_CREDIT_TYPE_ID_INT, EP_SK_TERRITORY_ID_INT , QUOTA_FLAG , BK_SALES_CREDIT_TYPE_CODE , BK_FISCAL_MONTH_NUMBER_INT , BK_FISCAL_YEAR_NUMBER_INT , DV_FISCAL_YEAR_MONTH_NUM_INT , EVENT_TYPE , SALES_REP_NUMBER , SALES_TERRITORY_KEY , SK_SC_AGENT_ID_INT , SK_RTR_ATTRIBUTION_ID , TOTAL_SPLIT_PCT , SALES_MOTION_CD , BK_OFFER_ATTRIBUTION_ID_INT FROM $$STGDB.WI_REV_SCA_NRT_SMR_INCR;
COLLECT STATS ON $$STGDB.WI_REV_SCA_NRT_SMR_PRE;
UPDATE JCT FROM $$ETLVWDB.DW_JOB_STREAMS JCT, (SELECT MAX(EDW_UPDATE_DTM) AS MAX_EDW_UPDATE_DTM FROM $$STGDB.WI_REV_SCA_NRT_SMR_INCR )WI_SCA SET LAST_EXTRACT_DATE = WI_SCA.MAX_EDW_UPDATE_DTM WHERE JCT.JOB_STREAM_ID = 'Wf_WI_REV_SCA_NRT_SMR_TV' AND WI_SCA.MAX_EDW_UPDATE_DTM IS NOT NULL;
