/* Added as part of December RTNR release */ DELETE FROM $$NRTSTGDB.WI_SUMMARY_QUOTE_SMC ALL;
INSERT INTO $$NRTSTGDB.WI_SUMMARY_QUOTE_SMC SELECT NRT.SALES_ORDER_LINE_KEY, WI.DV_EFFECTIVE_DTM, CASE WHEN WI.SO_CNT > 1 THEN 'MIX' ELSE WI.SALES_MOTION_CD END SALES_MOTION_CD, USER EDW_CREATE_USER, WI.EDW_UPDATE_DTM AS EDW_CREATE_DTM FROM ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, MAX(DV_EFFECTIVE_DTM) OVER(PARTITION BY SALES_ORDER_LINE_KEY) AS DV_EFFECTIVE_DTM, MAX(EDW_UPDATE_DTM) OVER(PARTITION BY SALES_ORDER_LINE_KEY) AS EDW_UPDATE_DTM, COUNT(*) OVER(PARTITION BY SALES_ORDER_LINE_KEY) SO_CNT, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY) DV_ALLOCATION_PCT FROM ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_EFFECTIVE_DTM, EDW_UPDATE_DTM, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY ) SUM_ALLOCATION_PCT, CAST(DV_ALLOCATION_PCT/SUM_ALLOCATION_PCT AS DECIMAL(18,6) ) AS DV_ALLOCATION_PCT /* Included inner query part to handle CS SQ scenario Sep 26 Q2FY20 Release */ FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC WHERE DV_EXPIRATION_DTM = '3500-01-01 00:00:00' AND EDW_UPDATE_DTM > ( SELECT LAST_EXTRACT_DATE FROM $$ETLVWDB.CTL_ETL_LAST_EXTRACT_DATES WHERE JOB_STREAM_ID = 'wf_N_SALES_ORDER_LINE_NRT_HIST_CG1_TV' AND TABLE_NAME = 'WI_SUMMARY_QUOTE_SMC' ) /* For identifying all open records for a particular allocation change*/ ) WI ) WI INNER JOIN $$SLSORDVWDB.N_SALES_ORDER_LINE NSOL /* Added to get line id for making sure prod2 also is updating basing on same line id*/ ON NSOL.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY INNER JOIN $$NRTNCRVWDB.N_SALES_ORDER_LINE_NRT_HIST_TV NRT ON NRT.SK_SALES_ORDER_LINE_ID_INT = NSOL.SK_SO_LINE_ID_INT AND NRT.SS_CD = NSOL.SS_CODE AND NRT.END_TV_DTM = '3500-01-01 00:00:00' INNER JOIN ( SELECT PROCESS_DT FROM $$NRTNCRVWDB.N_NRT_BKG_PROCESS_DT_CTRL WHERE SS_CD = 'CG' ) CTRL ON 1=1 AND ( CASE WHEN ( CURRENT_TIMESTAMP(0) < ( CAST( CURRENT_DATE AS TIMESTAMP(0)) + INTERVAL '21:00:00' HOUR TO SECOND )) AND (CURRENT_DATE = CTRL.PROCESS_DT) THEN 'TRUE' WHEN ( CURRENT_TIMESTAMP(0) >= ( CAST( CURRENT_DATE AS TIMESTAMP(0)) + INTERVAL '21:00:00' HOUR TO SECOND )) AND (CURRENT_DATE +1 = CTRL.PROCESS_DT) THEN 'TRUE' ELSE 'FALSE' END ) = 'TRUE' WHERE ROUND(DV_ALLOCATION_PCT) = 1.00 GROUP BY 1,2,3,4,5 ;
CALL COLLECT_STATS_WRAP('$$NRTSTGDB', 'WI_SUMMARY_QUOTE_SMC', 'D') ;
