ATTRIBUTE.VALUE:
COLLECT STATS $$STGDB.WI_REV_SCA_NRT_TV;
DELETE FROM $$STGDB.WI_BKG_SCA_NRT_TV ALL;
INSERT INTO $$STGDB.WI_BKG_SCA_NRT_TV ( SALES_CREDIT_ASSIGNMENT_KEY , SCA_SOURCE_TYPE_CD , SK_LINE_SEQ_ID_INT , SS_CD , EP_SOURCE_LINE_ID_INT , EP_SK_SALESREP_ID_INT , SCA_SALES_COMMISSION_PCT , START_TV_DTM , END_TV_DTM , SCA_SOURCE_COMMIT_DTM , EDW_CREATE_DTM , EDW_UPDATE_DTM , RU_SALES_ORDER_LINE_KEY , RU_AR_TRANSACTION_LINE_KEY , EP_SK_SALES_CREDIT_TYPE_ID_INT , EP_SK_TERRITORY_ID_INT , QUOTA_FLAG , BK_SALES_CREDIT_TYPE_CODE , BK_FISCAL_MONTH_NUMBER_INT , BK_FISCAL_YEAR_NUMBER_INT , DV_FISCAL_YEAR_MONTH_NUM_INT , EVENT_TYPE , /* RCHETTRI : REBOK CHANGES : ADDED 2 COLUMNS */ SALES_REP_NUMBER , SALES_TERRITORY_KEY , SK_SC_AGENT_ID_INT, SALES_MOTION_CD ) SELECT WI.SALES_CREDIT_ASSIGNMENT_KEY , WI.SCA_SOURCE_TYPE_CD , WI.SK_LINE_SEQ_ID_INT , WI.SS_CD , WI.EP_SOURCE_LINE_ID_INT , WI.EP_SK_SALESREP_ID_INT , WI.SCA_SALES_COMMISSION_PCT , WI.START_TV_DTM , WI.END_TV_DTM , WI.SCA_SOURCE_COMMIT_DTM , WI.EDW_CREATE_DTM , WI.EDW_UPDATE_DTM , WI.RU_SALES_ORDER_LINE_KEY , WI.RU_AR_TRANSACTION_LINE_KEY , WI.EP_SK_SALES_CREDIT_TYPE_ID_INT , WI.EP_SK_TERRITORY_ID_INT , WI.QUOTA_FLAG , WI.BK_SALES_CREDIT_TYPE_CODE , WI.BK_FISCAL_MONTH_NUMBER_INT , WI.BK_FISCAL_YEAR_NUMBER_INT , WI.DV_FISCAL_YEAR_MONTH_NUM_INT , WI.EVENT_TYPE , /* RCHETTRI : REBOK CHANGES : ADDED 2 COLUMNS */ WI.SALES_REP_NUMBER , WI.SALES_TERRITORY_KEY , WI.SK_SC_AGENT_ID_INT , COALESCE(SMC.SALES_MOTION_CD , 'UNKNOWN' ) AS SALES_MOTION_CD FROM $$STGDB.WI_REV_SCA_NRT_TV WI LEFT JOIN $$STGDB.WI_AR_SALES_MOTION_CHANGE SMC ON WI.RU_SALES_ORDER_LINE_KEY = SMC.SALES_ORDER_LINE_KEY;
COLLECT STATS $$STGDB.WI_BKG_SCA_NRT_TV;
/* UPDATE SC_LAST_PROCESS_DATE TO MAKE IT READY FOR NEXT RUN */ UPDATE EL FROM $$ETLVWDB.EL_AR_MONTH_PROCESS_DATE EL, (SELECT MAX(EDW_UPDATE_DTM) AS MAX_EDW_UPDATE_DTM FROM $$STGDB.WI_REV_SCA_NRT_TV ) WI_SCA SET SC_LAST_PROCESS_DATE = WI_SCA.MAX_EDW_UPDATE_DTM WHERE WI_SCA.MAX_EDW_UPDATE_DTM IS NOT NULL;
/* UPDATE LAST_EXTRACT_DATE TO MAKE IT READY FOR NEXT RUN */ UPDATE JCT FROM $$ETLVWDB.DW_JOB_STREAMS JCT, (SELECT MAX(EDW_UPDATE_DTM) AS MAX_EDW_UPDATE_DTM FROM $$STGDB.WI_REV_SCA_NRT_TV )WI_SCA SET LAST_EXTRACT_DATE = WI_SCA.MAX_EDW_UPDATE_DTM WHERE JCT.JOB_STREAM_ID = 'Wf_WI_REV_SCA_NRT_TV' AND WI_SCA.MAX_EDW_UPDATE_DTM IS NOT NULL;

DELETE FROM $$STGDB.WI_REV_SCA_NRT_INCR ALL;
/* Added as part of December RTNR release */ DELETE FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMC ALL;
INSERT INTO $$STGDB.WI_AR_SUMMARY_QUOTE_SMC SELECT SALES_ORDER_LINE_KEY, DV_EFFECTIVE_DTM, CASE WHEN SO_CNT > 1 THEN 'MIX' ELSE SALES_MOTION_CD END SALES_MOTION_CD, USER EDW_CREATE_USER, CURRENT_TIMESTAMP(0) EDW_CREATE_DTM FROM ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, MAX(DV_EFFECTIVE_DTM) OVER(PARTITION BY SALES_ORDER_LINE_KEY) AS DV_EFFECTIVE_DTM, COUNT(*) OVER(PARTITION BY SALES_ORDER_LINE_KEY) SO_CNT, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY) DV_ALLOCATION_PCT FROM ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_EFFECTIVE_DTM, EDW_UPDATE_DTM, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY ) SUM_ALLOCATION_PCT, CAST( DV_ALLOCATION_PCT/SUM_ALLOCATION_PCT AS DECIMAL(18,6) ) AS DV_ALLOCATION_PCT /* Included inner query part to handle CS SQ scenario Sep 26 Q2FY20 Release */ FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC WHERE DV_EXPIRATION_DTM = '3500-01-01 00:00:00' AND SALES_ORDER_LINE_KEY IN ( SELECT SALES_ORDER_LINE_KEY FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC WHERE EDW_UPDATE_DTM > ( SELECT LAST_EXTRACT_DATE FROM $$ETLVWDB.CTL_ETL_LAST_EXTRACT_DATES WHERE JOB_STREAM_ID = 'wf_WI_REV_SCA_NRT_TV' AND TABLE_NAME = 'WI_AR_SUMMARY_QUOTE_SMC' ) ) /* For identifying all open records for a particular allocation change*/ ) WI ) WI WHERE ROUND(DV_ALLOCATION_PCT) = 1.00 GROUP BY 1,2,3,4,5 ;
CALL COLLECT_STATS_WRAP('$$STGDB', 'WI_AR_SUMMARY_QUOTE_SMC', 'D') ;
UPDATE WI_AR FROM $$STGDB.WI_AR_SALES_MOTION_CHANGE WI_AR, ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD , DV_EFFECTIVE_DTM FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMC ) WI SET SALES_MOTION_CD = WI.SALES_MOTION_CD , SOURCE_COMMIT_TIME = WI.DV_EFFECTIVE_DTM, EDW_UPDATE_DTM = CURRENT_TIMESTAMP(0) WHERE WI_AR.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY AND WI_AR.EDW_UPDATE_DTM < WI.DV_EFFECTIVE_DTM;
UPDATE EL FROM $$ETLVWDB.CTL_ETL_LAST_EXTRACT_DATES EL, ( SELECT MAX(EDW_UPDATE_DTM) MAX_UPD_DATE FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC ) NSOL SET LAST_EXTRACT_DATE = COALESCE(NSOL.MAX_UPD_DATE , EL.LAST_EXTRACT_DATE ) WHERE JOB_STREAM_ID = 'wf_WI_REV_SCA_NRT_TV' AND TABLE_NAME = 'WI_AR_SUMMARY_QUOTE_SMC';
/* Added as part of December RTNR release */
UPDATE WI FROM $$STGDB.WI_AR_SALES_MOTION_CHANGE WI , ( SELECT SALES_ORDER_LINE_KEY, SK_SALES_ORDER_LINE_ID_INT, SALES_MOTION_CD, SOURCE_COMMIT_DTM FROM $$NRTNCRVWDB.N_SALES_ORDER_LINE_NRT_HIST_TV NSOL WHERE NSOL.END_TV_DTM = '3500-01-01 00:00:00' AND NSOL.SS_CD <> 'OPL' AND NSOL.SALES_ORDER_LINE_KEY NOT IN ( SELECT SALES_ORDER_LINE_KEY FROM ( SELECT SALES_ORDER_LINE_KEY, SALES_MOTION_CD, DV_EFFECTIVE_DTM, EDW_UPDATE_DTM, SUM(DV_ALLOCATION_PCT) OVER(PARTITION BY SALES_ORDER_LINE_KEY ) SUM_ALLOCATION_PCT, CAST( DV_ALLOCATION_PCT/SUM_ALLOCATION_PCT AS DECIMAL(18,6) ) AS DV_ALLOCATION_PCT /* Included inner query part to handle CS SQ scenario Sep 26 Q2FY20 Release */ FROM $$SLSORDVWDB.MT_SUMMARY_QUOTE_SOL_ALLOC WHERE DV_EXPIRATION_DTM = '3500-01-01 00:00:00' ) WI GROUP BY 1 HAVING ROUND(SUM(DV_ALLOCATION_PCT)) = 1.00 ) AND NSOL.EDW_UPDATE_DTM > CURRENT_DATE - 7 ) NSOL SET SALES_MOTION_CD = NSOL.SALES_MOTION_CD , SOURCE_COMMIT_TIME = NSOL.SOURCE_COMMIT_DTM, EDW_UPDATE_DTM = CURRENT_TIMESTAMP(0) WHERE WI.SALES_ORDER_LINE_KEY = NSOL.SALES_ORDER_LINE_KEY AND WI.SALES_MOTION_CD <> NSOL.SALES_MOTION_CD ;
INSERT INTO $$STGDB.WI_AR_SALES_MOTION_CHANGE SELECT SALES_ORDER_LINE_KEY, SK_SALES_ORDER_LINE_ID_INT, SALES_MOTION_CD, SOURCE_COMMIT_DTM , CURRENT_TIMESTAMP(0) AS EDW_CREATE_DTM, CURRENT_TIMESTAMP(0) AS EDW_UPDATE_DTM FROM ( SELECT SALES_ORDER_LINE_KEY, SK_SALES_ORDER_LINE_ID_INT, SALES_MOTION_CD, SOURCE_COMMIT_DTM FROM $$NRTNCRVWDB.N_SALES_ORDER_LINE_NRT_HIST_TV NSOL WHERE NSOL.END_TV_DTM = '3500-01-01 00:00:00' AND NSOL.SS_CD <> 'OPL' AND NSOL.EDW_UPDATE_DTM > CURRENT_DATE - 7 AND NSOL.SALES_MOTION_CD <> 'UNKNOWN' UNION SELECT NSOL1.SALES_ORDER_LINE_KEY, NSOL1.SK_SALES_ORDER_LINE_ID_INT, NSOL1.SALES_MOTION_CD, NSOL1.SOURCE_COMMIT_DTM FROM $$STGDB.WI_AR_SUMMARY_QUOTE_SMC SMC INNER JOIN $$NRTNCRVWDB.N_SALES_ORDER_LINE_NRT_HIST_TV NSOL1 ON SMC.SALES_ORDER_LINE_KEY = NSOL1.SALES_ORDER_LINE_KEY AND NSOL1.SS_CD <> 'OPL' AND NSOL1.END_TV_DTM = '3500-01-01 00:00:00' ) WI WHERE NOT EXISTS ( SELECT 1 FROM $$STGDB.WI_AR_SALES_MOTION_CHANGE WI_AR WHERE WI_AR.SALES_ORDER_LINE_KEY = WI.SALES_ORDER_LINE_KEY ) ;
COLLECT STATS ON $$STGDB.WI_AR_SALES_MOTION_CHANGE ;
